                    placeholder="Search Products"
                />
               }
                <View
                  style={{
                    justifyContent: "center",
                    alignItems: "flex-end",
                    flexDirection: "row",
                    flexWrap: "wrap",
                  }}
                >
                  { Object.keys(arrayCat).map((group:any,i) => {
                    return (
                      <View >
                        {props.showCategories &&
                          <Text style={[styles.textBlack]}>{group}</Text>
                        }
                          
