                    flexWrap: "wrap",
                  }}
                >
                  { Object.keys(arrayCat).map((group:any,i) => {
                    return (
                      <View >
                        {props.showCategories &&
                          <Text style={[styles.textBlack]}>{group}</Text>
                        }
                          
                          {renderCategoryItems(arrayCat[group],i)}
                        </View>
                      ) 
                    })
                  }
                   </View>
                  </ScrollView>
             
              </View>
          }

          { openDrawer && props.location == "Top" &&
              <View style={styles.overlayFromTop}>
                
                <ScrollView>
                {props.showTitle &&
                  <Text style={[styles.menuHeader]}>{props.name}</Text>
                }

                {props.closeButtonIsShowing &&
                <TouchableOpacity style={styles.closeBtn} onPress={onClose}>
                <View style = {styles.btnTextBackGroundView}>
                    <Text>X</Text>
                    </View>
            </TouchableOpacity>
       
                // <View style={[styles.closeBtn]}>
                //      <Button onPress={ onClose } title = "x" color='transparent'/>
                // </View>
                }
               
               {props.searchIsEnabled &&
                  <TextInput style={styles.textInputStyle}
                     onChangeText={(searchText) => handleSearchChange(searchText)}
                     // value={props.searchText}
                     underlineColorAndroid="transparent"
                    placeholder="Search Products"
                />
               }
                <View
                  style={{
                    justifyContent: "center",
                    alignItems: "flex-end",
                    flexDirection: "row",
                    flexWrap: "wrap",
                  }}
                >
                  { Object.keys(arrayCat).map((group:any,i) => {
                    return (
                      <View >
                        {props.showCategories &&
                          <Text style={[styles.textBlack]}>{group}</Text>
                        }
                          
                          {renderCategoryItems(arrayCat[group],i)}
                        </View>
                      ) 
                    })
                  }
                   </View>
                  </ScrollView>
              </View>
          }
          { openDrawer && props.location == "Bottom" &&
              <View style={styles.overlayFromBottom}>
                
                <ScrollView>
                {props.showTitle &&
                  <Text style={[styles.menuHeader]}>{props.name}</Text>
                }

                {props.closeButtonIsShowing &&
                <TouchableOpacity style={styles.closeBtn} onPress={onClose}>
                <View style = {styles.btnTextBackGroundView}>
                    <Text>X</Text>
                    </View>
            </TouchableOpacity>
       
                // <View style={[styles.closeBtn]}>
                //      <Button onPress={ onClose } title = "x" color='transparent'/>
                // </View>
                }
               
               {props.searchIsEnabled &&
                  <TextInput style={styles.textInputStyle}
                     onChangeText={(searchText) => handleSearchChange(searchText)}
                     // value={props.searchText}
                     underlineColorAndroid="transparent"
                    placeholder="Search Products"
                />
               }
                <View
                  style={{
                    justifyContent: "center",
                    alignItems: "flex-end",
                    flexDirection: "row",
                    flexWrap: "wrap",
                  }}
                >
                  { Object.keys(arrayCat).map((group:any,i) => {
                    return (
                      <View >
                        {props.showCategories &&
                          <Text style={[styles.textBlack]}>{group}</Text>
                        }
                          
                          {renderCategoryItems(arrayCat[group],i)}
                        </View>
                      ) 
                    })
                  }
                   </View>
                  </ScrollView>
              </View>
          }

{ openDrawer && props.location == "Inline" &&
              <View style={styles.overlayFromInline}>
                 <ScrollView>
                {props.showTitle &&
                  <Text style={[styles.menuHeader]}>{props.name}</Text>
                }

                {props.closeButtonIsShowing &&
                <TouchableOpacity style={styles.closeBtn} onPress={onClose}>
                <View style = {styles.btnTextBackGroundView}>
                    <Text>X</Text>
                    </View>
            </TouchableOpacity>
       
                // <View style={[styles.closeBtn]}>
                //      <Button onPress={ onClose } title = "x" color='transparent'/>
                // </View>
                }
               
               {props.searchIsEnabled &&
                  <TextInput style={styles.textInputStyle}
                     onChangeText={(searchText) => handleSearchChange(searchText)}
                     // value={props.searchText}
                     underlineColorAndroid="transparent"
                    placeholder="Search Products"
                />
               }
                <View
                  style={{
                    justifyContent: "center",
                    alignItems: "flex-end",
                    flexDirection: "row",
                    flexWrap: "wrap",
                  }}
                >
                  { Object.keys(arrayCat).map((group:any,i) => {
                    return (
                      <View >
