import React, { useState } from 'react';
import {Image,TextInput,View, Text, StyleSheet,TouchableOpacity, Dimensions, FlatList, SafeAreaView, Button, ScrollView} from 'react-native';

const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;
import Draggy from '../../../src/Draggy';
import {
  DragContainer,
 
  DropZone
} from "react-native-drag-drop-and-swap";



/* Canvas props */
interface canvasProps {
  location: string,
  name:string
  items: any,
  showCategories: boolean,
  showTitle: boolean,
  searchIsEnabled: boolean,
  disableDragAndDrop: boolean,
  title:string,
  closeButtonIsShowing:boolean,
  dndName:string,
  onDrop?: any,
  onHover?: any,
  dragOver?: any,
  alphabet?:any,
  index?: any
