import React, { useState } from 'react';
import {Image,TextInput, Animated,View, Text, StyleSheet,TouchableOpacity, Dimensions, FlatList, SafeAreaView, Button, ScrollView} from 'react-native';

const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;
import { createDndContext } from "react-native-easy-dnd";

const { Provider, Droppable, Draggable } = createDndContext();

/* Canvas props */
interface canvasProps {
  location: string,
  name:string
  items: any,
  showCategories: boolean,
  showTitle: boolean,
  searchIsEnabled: boolean,
  disableDragAndDrop: boolean,
  title:string,
  closeButtonIsShowing:boolean,
  dndName:string
}


/* Canvas view with drawer options*/
const Canvas = (props:canvasProps) => {
  const droppableOpacity = React.useRef(new Animated.Value(0));
  const trashIconScale = React.useRef(new Animated.Value(1));
  const [droppedItem, setDroppedItem] = useState([]);
  const [openDrawer, setOpenDrawer] = useState(false);
  const animateValue = (ref: React.MutableRefObject<Animated.Value>, toValue: number) =>
    Animated.timing(ref.current, {
      useNativeDriver:true,
      toValue,
      duration: 300
    }).start();

    
  /**
   * Grop item by category
   */
    const groupBy = (xs: any, key: string) => {
      return xs.reduce(function (rv: any, x: any) {
        (rv[x[key]] = rv[x[key]] || []).push(x)
        return rv
      }, {})
    }
    
  

    // events define

  /**
   * Function is called on item drag
   * @param item 
   */
    const onItemDrag=()=>{
      console.log("onItemDrag");
    }

  /**
   * FUnction is called on item drop
   */
    const onItemDrop=()=>{
      console.log("onItemDrop");
    }

  /**
   * FUnction is called on item click
   */
    const onItemClick=()=>{
      console.log("onItemClick");
    }

  /**
   * FUnction is called on item search
   */
    const onSearch=()=>{
      console.log("onSearch");
    }

   /**
   * On click of button closes the drawer
   */
