    const renderCategoryItems = (arrayCatItems: any) => {
        return (
          <FlatList
            data={arrayCatItems}
            renderItem={({ item }) => (  
                <Draggable
                   key={item}
                   onDragStart={() => {
                     console.log("Item drage start");
                     onItemDrag();
                     animateValue(droppableOpacity, 1);
                   }}
                   onDragEnd={() => {
                    console.log("Item drage End");
                    onItemDrop();
                      animateValue(droppableOpacity, 0);
                    }}
                   payload={item}
                >
           
           {({ viewProps }) => {
             return (
               <Animated.View
                 {...viewProps}
                 style={[viewProps.style, styles.draggable]}
               >
                 {/* <View onPress={onItemClick()}> */}
                 <View>
                   <Image style={{width:30,height:30}}  source={{uri : item.image}}/>
                    <Text style={{ color: "#333", fontWeight: "bold" }}>
                      {item.name}
                    </Text>
                 </View>
               </Animated.View>
             );
           }}
         </Draggable>                   
        )}
            //Setting the number of column
            numColumns={2}
            keyExtractor={(item, index) => index.toString()}
          />
        );       
    }
  


